import React, { Component } from 'react';
import './App.css';
import Button from './component/Button';
import axios from 'axios';

class App extends Component {

  state = {
    posts: [],
    data: []
  }

  componentDidMount() {
    axios.get('https://test.wertkt.com/api/biz/')
    .then(response => {
      this.setState( { posts: response.data } )
    })
    .catch(error => {
      console.log(error);
    });
  }
  render(){
    let filter = this.props.state.posts
    return (
      <div className="box">
        <nav className="nav">
          <h1 className="nav__title">Dashboard</h1>
          <h2 className="nav__subtitle">Liste des entreprises :</h2>
          <ul className="nav__list">
            { this.state.posts.map( item => <li key={item.id}>{item.name}</li>)}
          </ul>
        </nav>
        <div className="dashboard">
          <p className="dashboard__title">Rechercher une entreprise</p>
          <input className="dashboard__input" type='text' />
          <button className="dashboard__button">rechercher</button>
          <Button />
        </div>
      </div>
    );
  }
}

export default App;
