import React, { Component } from 'react';
import './App.css';
import Button from './component/Button';
import axios from 'axios';

class App extends Component {

  state = {
    posts: [],
    data: [],
    // inputType: ''
  }

  componentDidMount() {
    axios.get('https://test.wertkt.com/api/biz/')
    .then(response => {
      this.setState( { posts: response.data } )
    })
    .catch(error => {
      console.log(error);
    });
  }
  test = () => {
    console.log('ok');
    return(
      <h1>Salut</h1>
    )
  } 
  render(){
    // let filteredArray = this.state.posts.filter(post => {
    //   return post === 
    // });
    return (
      <div className="box">
        <nav className="nav">
          <h1 className="nav__title">Dashboard</h1>
          <h2 className="nav__subtitle">Liste des entreprises :</h2>
          <ul className="nav__list">
            { this.state.posts.map( item => <li key={item.id} onClick={this.test} >{item.name}</li>)}
          </ul>
        </nav>
        <div className="dashboard">
          <p className="dashboard__title">Rechercher une entreprise</p>
          <input className="dashboard__input" type='text'  />    
          <button className="dashboard__button">rechercher</button>
          <Button />
        </div>
      </div>
    );
  }
}

export default App;
