import React, { Component } from 'react';
import './App.css';
import Button from './component/Button';
import axios from 'axios';

class App extends Component {

  state = {
    companys: [],
    data: [],
    isShow: false,
    search: ''
  }


  componentDidMount() {
    axios.get('https://test.wertkt.com/api/biz/')
    .then(response => {
      this.setState( { companys: response.data } )
    })
    .catch(error => {
      console.log(error);
    });
  }
  handleShow = () => {
    const isShow = !this.state.isShow
    this.setState({isShow}) 
  }
  updateSearch(event) {
    this.setState({search: event.target})
  }
  render(){
    // let filteredArray = this.state.companys.filter(company => {
    //   return company === 
    // });
    const {isShow} = this.state

    let description = null
    let name = null

    if(isShow){
      name = this.state.companys.map(obj => <p key={obj.id} > {obj.name}</p>) /*  Trouver comment renvoyer un élément précise d'un objet du tableau en fonction du click */
      description = (<Button />)
    }
    return (

      <div className="box">
        <nav className="nav">
          <h1 className="nav__title">Dashboard</h1>
          <h2 className="nav__subtitle">Liste des entreprises :</h2>
          <ul className="nav__list">
            {
              this.state.companys.map(company => {
                return (<li key={company.id} onClick={this.handleShow} >{company.name}</li>)}
              )
            }
          </ul>
        </nav>
        <div className="dashboard">
          <p className="dashboard__title">Rechercher une entreprise</p>
          <input className="dashboard__input" type='text'  />    
          <button className="dashboard__button">rechercher</button>
          {}<div>

          </div>
          { name }
          { description }
          {/* {
            this.state.companys.find(item => <li key={item.id}>{item.name} {item.sector} {item.siren}</li>)
          } */}

        </div>
      </div>
    );
  }
}
export default App;


